'    );',
'',
'END p_render;',
'',
'PROCEDURE P_AJAX (',
'    p_plugin IN APEX_PLUGIN.T_PLUGIN,',
'    p_region IN APEX_PLUGIN.T_REGION,',
'    p_param  IN APEX_PLUGIN.T_REGION_AJAX_PARAM,',
'    p_result IN OUT NOCOPY APEX_PLUGIN.T_REGION_AJAX_RESULT',
') IS',
'    l_handler           VARCHAR2(30)   := APEX_APPLICATION.G_X02;   -- ''INITIAL'' veya ''LOAD''',
'    l_parent_id         VARCHAR2(4000) := APEX_APPLICATION.G_X01;   -- x01',
'    l_sql               CLOB;',
'    l_ctx               apex_exec.t_context;',
'    l_params            apex_exec.t_parameters := apex_exec.c_empty_parameters;',
'',
'    l_init_sql          Clob := p_region.attributes.get_varchar2(''init_sql'');',
'    l_load_sql          Clob := p_region.attributes.get_varchar2(''load_sql'');',
'    l_on_drop_plsql     Clob := p_region.attributes.get_varchar2(''on_drop_plsql'');',
'',
'    l_success           BOOLEAN := true;',
'BEGIN',
'    -- JSON header',
'    APEX_PLUGIN_UTIL.PRINT_JSON_HTTP_HEADER;',
'',
'    -- Hangi SQL?',
'    IF l_handler = ''INITIAL'' THEN',
'        l_sql := l_init_sql;  -- INIT_SQL',
'        ',
'        l_ctx := apex_exec.open_query_context(',
'           p_location        => apex_exec.c_location_local_db,',
'           p_sql_query       => l_sql,',
'           p_sql_parameters  => l_params,',
'           p_auto_bind_items => TRUE',
'        );',
'',
'        apex_json.open_object;',
'        apex_json.write_context( ''row'', l_ctx );',
'        apex_json.close_object;',
'',
'    ELSIF l_handler = ''LOAD'' THEN',
'        l_sql := l_load_sql;  ',
'        IF INSTR(l_load_sql, ''TREE$PARENT_ID'') > 0 THEN',
'            apex_exec.add_parameter(p_parameters => l_params, p_name => ''TREE$PARENT_ID'', p_value => l_parent_id);',
'        END IF;',
'',
'        l_ctx := apex_exec.open_query_context(',
'           p_location        => apex_exec.c_location_local_db,',
'           p_sql_query       => l_sql,',
'           p_sql_parameters  => l_params,',
'           p_auto_bind_items => TRUE',
'        );',
'',
'        apex_json.open_object;',
'        apex_json.write_context( ''row'', l_ctx );',
'        apex_json.close_object;',
'',
'    ELSIF l_handler = ''ON_DROP'' THEN',
'        DECLARE',
'            l_node_id       VARCHAR2(4000) := APEX_APPLICATION.G_X01;',
'            l_old_parent    VARCHAR2(4000) := APEX_APPLICATION.G_X03;',
'            l_new_parent    VARCHAR2(4000) := APEX_APPLICATION.G_X04;',
'            l_old_position  NUMBER         := TO_NUMBER(NVL(APEX_APPLICATION.G_X05, ''0''));',
'            l_new_position  NUMBER         := TO_NUMBER(NVL(APEX_APPLICATION.G_X06, ''0''));',
'            l_old_parent_type       VARCHAR2(4000) := APEX_APPLICATION.G_X07;',
'            l_new_parent_type    VARCHAR2(4000) := APEX_APPLICATION.G_X08;',
'            l_node_type    VARCHAR2(4000) := APEX_APPLICATION.G_X09;',
'        BEGIN',
unistr('            -- Ko\015Fullu parametre eklemeleri'),
'            IF INSTR(l_on_drop_plsql, ''TREE$NODE_ID'') > 0 THEN',
'                apex_exec.add_parameter(p_parameters => l_params, p_name => ''TREE$NODE_ID'', p_value => l_node_id);',
'            END IF;',
'',
'            IF INSTR(l_on_drop_plsql, ''TREE$OLD_PARENT_ID'') > 0 THEN',
'                apex_exec.add_parameter(p_parameters => l_params, p_name => ''TREE$OLD_PARENT_ID'', p_value => l_old_parent);',
'            END IF;',
'',
'            IF INSTR(l_on_drop_plsql, ''TREE$NEW_PARENT_ID'') > 0 THEN',
'                apex_exec.add_parameter(p_parameters => l_params, p_name => ''TREE$NEW_PARENT_ID'', p_value => l_new_parent);',
'            END IF;',
'',
'            IF INSTR(l_on_drop_plsql, ''TREE$OLD_POSITION'') > 0 THEN',
'                apex_exec.add_parameter(p_parameters => l_params, p_name => ''TREE$OLD_POSITION'', p_value => TO_CHAR(l_old_position));',
'            END IF;',
'',
'            IF INSTR(l_on_drop_plsql, ''TREE$NEW_POSITION'') > 0 THEN',
'                apex_exec.add_parameter(p_parameters => l_params, p_name => ''TREE$NEW_POSITION'', p_value => TO_CHAR(l_new_position));',
'            END IF;',
'',
'            IF INSTR(l_on_drop_plsql, ''TREE$OLD_PARENT_TYPE'') > 0 THEN',
'                apex_exec.add_parameter(p_parameters => l_params, p_name => ''TREE$OLD_PARENT_TYPE'', p_value => l_old_parent_type);',
'            END IF;',
'',
'            IF INSTR(l_on_drop_plsql, ''TREE$NEW_PARENT_TYPE'') > 0 THEN',
'                apex_exec.add_parameter(p_parameters => l_params, p_name => ''TREE$NEW_PARENT_TYPE'', p_value => TO_CHAR(l_new_parent_type));',
'            END IF;',
'',
'            IF INSTR(l_on_drop_plsql, ''TREE$NODE_TYPE'') > 0 THEN',
'                apex_exec.add_parameter(p_parameters => l_params, p_name => ''TREE$NODE_TYPE'', p_value => TO_CHAR(l_node_type));',
'            END IF;',
'',
'            apex_exec.execute_plsql(',
'                p_plsql_code     => l_on_drop_plsql,',
'                p_sql_parameters => l_params',
'            );',
'        EXCEPTION',
'            WHEN OTHERS THEN',
'                l_success := FALSE;',
'        END;',
'        apex_json.open_object;',
'        apex_json.write( ''success'', l_success );',
'        apex_json.close_object;',
'    END IF;',
'',
'    ',
'END P_AJAX;'))
